import React, { useState } from 'react'; // <-- ADDED: useState import
import { Check, Copy } from 'lucide-react'; // <-- ADDED: Icon imports
import './Navbar.css';

// Navbar Component
const Navbar = ({ currentPage, setCurrentPage }) => {
  const [emailCopied, setEmailCopied] = useState(false);
  const [showTooltip, setShowTooltip] = useState(false);

  const handleCopyEmail = async () => {
    try {
      await navigator.clipboard.writeText('chae@example.com');
      setEmailCopied(true);
      setTimeout(() => setEmailCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy email:', err);
    }
  };

  return (
    <nav className="fixed top-0 w-full z-50 bg-white/70 backdrop-blur-md border-b border-gray-100">
      <div className="max-w-6xl mx-auto px-6 py-4 flex items-center justify-between">
        <div className="flex items-center space-x-8">
          <button
            onClick={() => setCurrentPage('home')}
            className={`text-sm font-medium transition-colors ${
              currentPage === 'home' ? 'text-black' : 'text-gray-500 hover:text-black'
            }`}
          >
            • Work
          </button>
          <button
            onClick={() => setCurrentPage('about')}
            className={`text-sm font-medium transition-colors ${
              currentPage === 'about' ? 'text-black' : 'text-gray-500 hover:text-black'
            }`}
          >
          About
          </button>
          <a
            href="#"
            className="text-sm font-medium text-gray-500 hover:text-black transition-colors"
          >
            Résumé
          </a>
        </div>

        <div className="relative">
          <button
            onClick={handleCopyEmail}
            onMouseEnter={() => setShowTooltip(true)}
            onMouseLeave={() => setShowTooltip(false)}
            className="bg-black text-white px-4 py-2 rounded-full text-sm font-medium hover:bg-gray-800 transition-all flex items-center space-x-2"
          >
            {emailCopied ? <Check size={16} /> : <Copy size={16} />}
            <span>Copy email</span>
          </button>

          {(showTooltip || emailCopied) && (
            <div className="absolute top-full mt-2 left-1/2 transform -translate-x-1/2 bg-black text-white text-xs px-2 py-1 rounded whitespace-nowrap">
              {emailCopied ? 'Email copied!' : 'Email copied!'}
              <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-black rotate-45"></div>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar; // <-- ADDED: The export statement
